jQuery(document).ready(function() {

	jQuery(".container , footer").fitVids();

//prettyPhoto Setup
	jQuery(".gallery-images .gallery-icon a").attr('rel' , 'prettyPhoto[gallery]');
    jQuery("a[rel^='prettyPhoto']").prettyPhoto({animation_speed:'normal',theme: tie.prettyPhoto });
	
	jQuery.fn.prettyPhoto({'theme': tie.prettyPhoto });
	jQuery(document).on("click", "a[rel^='prettyPhoto']" , function() {
		jQuery.prettyPhoto.open(jQuery(this).attr("href"),"","");
		return false;
	});

//Scroll To top
	jQuery(window).scroll(function(){
		if (jQuery(this).scrollTop() > 100) {
			jQuery('.scrollToTop').fadeIn();
		} else {
			jQuery('.scrollToTop').fadeOut();
		}
	});
	jQuery('.scrollToTop').click(function(){
		jQuery('html, body').animate({scrollTop: '0px'}, 800);
		return false;
	});
		
//Like Button	
	jQuery(document).on("click", "a.tie-likes" , function() {
		var post_id = jQuery(this).attr('rel');
		var like = jQuery(this);
			
		if( like.hasClass('liked') ){
			//alert( 'You have already loved this item.' )
			return false;
		}
		jQuery.post(tie.ajaxurl, { action:'tie_like_post' , post:post_id }, function(data) {
			like.html( data ).addClass('liked').attr('title','You already like this');
		}, 'html');
		return false;
	});
	
//Load-More Button	
    var load_more = jQuery('#load-more'), orig_text = load_more.text(),	page = tie.paged , query = tie.query , home = tie.isHome;
	load_more.click(function(){
		page++;
		//load_more.text( tie.loading );
		load_more.html( '<span class="load-more-img"></span>' );
		jQuery.post(tie.ajaxurl, { action:'tie_load_more' , page:page , query : query , isHome : home }, function(data) {
			var content = jQuery(data);
			content.fitVids();
			content.each(function(index){
				jQuery(this).removeClass("rtl-item");
				if(jQuery().mediaelementplayer){
					jQuery(this).find('.wp-audio-shortcode, .wp-video-shortcode').mediaelementplayer();
				}
				jQuery(this).find("a[rel^='prettyPhoto']").prettyPhoto({animation_speed:'normal',theme: tie.prettyPhoto });
			})
			
			$container.isotope({ filter: '*' });
			jQuery('#filters li').removeClass("current");
			jQuery('.all-items').addClass("current");
			
			//jQuery(content).imagesLoaded(function() {
    			jQuery('#main-content #grid').append( content ).isotope( 'appended', content, function() {
    			    jQuery('#main-content #grid').isotope('reLayout');
    			});
    			load_more.text(orig_text);
				if(page >= tie.maxPages) load_more.fadeOut();
			//});
			
			jQuery(content).imagesLoaded(function() {
				jQuery('#main-content #grid').isotope('reLayout');
			});
			
		}, 'html');
		return false;
	});


//Setup Home Masonry
	var $container = jQuery('#main-content #grid'),$body = jQuery('body'),	colW = tie.width, columns = null;
	$container.imagesLoaded(function(){
		$container.isotope({
			itemSelector : '.item-list',
			resizable: false,
			transformsEnabled: transforms,
			animationOptions: {
				duration: 400,
				easing: 'swing',
				queue: false
			},
			masonry: { /*columnWidth: tie.width*/ }
		});
		jQuery('#main-content #grid').isotope();
		jQuery('#main-content #grid article').removeClass('rtl-item');
	});
	
//Filter
	jQuery('#filters a').click(function(){
		var selector = jQuery(this).attr('data-filter');
		jQuery("#filters li").removeClass("current");
		jQuery(this).parent().addClass("current");
		$container.isotope({ filter: selector });
		return false;
	});
		
//Header
	jQuery('header').imagesLoaded(function(){
		var hedaerHeight = jQuery('header').height() ;
		var main = jQuery('header.trans');
		jQuery(window).scroll(function(){
			HeaderScroll();
		});
		jQuery(window).load(function(){
			HeaderScroll();
		});
		function HeaderScroll(){
			var scrollY=jQuery(window).scrollTop();
			if(main.length>0){
				if(scrollY > hedaerHeight){
					main.stop().addClass('trans-head');
				}else if(scrollY < hedaerHeight){
					main.removeClass('trans-head');
				}
			}
		}
		if(jQuery('header.fixed-head').length>0){
			jQuery('body').css('paddingTop', hedaerHeight+25);
		}
	});	
	

	jQuery(window).smartresize(function(){
		var currentColumns = Math.floor( ( $body.width() -10 ) / colW );
		if ( currentColumns !== columns ) {
			columns = currentColumns;
			jQuery(' #main-content #content , .header-content, .main-menu').width( ( columns * colW ) );
			jQuery('#main-content #grid').isotope();
		}
		
		$container.isotope({
			masonry: { }
		});
		
		var hedaerHeight = jQuery('header').height() ;
		if(jQuery('header.fixed-head').length>0){
			jQuery('body').css('paddingTop', hedaerHeight+25);
		}

	}).smartresize();
 

//Mobiles Menus
	jQuery('#main-menu-mob, #top-menu-mob').bind('change', function () {
          var url = jQuery(this).val();
          if (url) {
              window.location = url;
          }
          return false;
	});
	
//tooltip();
    jQuery('.ttip , .tooltip-n').tipsy({fade: true, gravity: 's'});
    jQuery('.tooldown, .tooltip-s').tipsy({fade: true, gravity: 'n'});
	
    jQuery('.tooltip-nw').tipsy({fade: true, gravity: 'nw'});
    jQuery('.tooltip-ne').tipsy({fade: true, gravity: 'ne'});	
    jQuery('.tooltip-w').tipsy({fade: true, gravity: 'w'});
    jQuery('.tooltip-e').tipsy({fade: true, gravity: 'e'});
    jQuery('.tooltip-sw').tipsy({fade: true, gravity: 'w'});
    jQuery('.tooltip-se').tipsy({fade: true, gravity: 'e'});
	
// Toggle Shortcode
	jQuery(".toggle-head-open").click(function () {
		jQuery(this).parent().find(".toggle-content").slideToggle("slow");
		jQuery(this).hide();
		jQuery(this).parent().find(".toggle-head-close").show();
    });
	jQuery(".toggle-head-close").click(function () {
		jQuery(this).parent().find(".toggle-content").slideToggle("slow");
		jQuery(this).hide();
		jQuery(this).parent().find(".toggle-head-open").show();
    });
	
		
	jQuery("#main-nav li , .top-menu li").each(function(){	
		var $sublist = jQuery(this).find('ul:first');		
		jQuery(this).hover(function(){	
			$sublist.stop().css({overflow:"hidden", height:"auto", display:"none"}).slideDown(200, function(){
				jQuery(this).css({overflow:"visible", height:"auto"});
			});	
		},
		function(){	
			$sublist.stop().slideUp(200, function()	{	
				jQuery(this).css({overflow:"hidden", display:"none"});
			});
		});	
	});

//tabbed Boxes
	jQuery("#tabbed-widget .tabs-wrap").hide();
	jQuery("#tabbed-widget ul.posts-taps li:first").addClass("active").show();
	jQuery("#tabbed-widget .tabs-wrap:first").show(); 
	jQuery("#tabbed-widget  li.tabs").click(function() {
		jQuery("#tabbed-widget ul.posts-taps li").removeClass("active");
		jQuery(this).addClass("active");
		jQuery("#tabbed-widget .tabs-wrap").hide();
		var activeTab = jQuery(this).find("a").attr("href");
		jQuery(activeTab).slideDown();
		return false;
	});

});

